% Understanding DSP with Matlab and solutions% example 1.1.3% 2018.11.16t = -12:0.05:12;s1 = sin(0.5*(t+eps))./(t+eps);s2 = sin(t+eps)./(t+eps);s3 = sin(2.5*(t+eps))./(t+eps);plot(t,s1,'k');hold on;plot(t,s2,'b');hold on;plot(t,s3,'r');grid on;set(gca,'FontSize',30);xlabel('t','fontsize',18);ylabel('sin(at)/t','fontsize',18);legend('sin(0.5t)/t','sin(t)/t','sin(2.5t)/t');