% Understanding DSP with Matlab and solutions% example 2.5.1% 2018.11.23N = 21;n = 0:N-1;w = 0:0.01:pi;wh = 0.54-0.46*cos(2*pi*n'/(N-1));f1 = abs((0.95.^n')' * exp(-j*n'*w));f1s = sum(f1,1);f2h = abs((0.95.^n'.*wh)'*exp(-j*n'*w));    f2hs = sum(f2h,1);fwe = abs(1./(1-0.95*exp(-j*w)));gcaFontSize = 24;labelSize = 18;subplot(2,3,1);plot(w,f1s,'r');set(gca,'FontSize',gcaFontSize);xlabel('\omega','fontsize',labelSize);ylabel('Magnitude','fontsize',labelSize);subplot(2,3,2);plot(w,f2hs,'r');set(gca,'FontSize',gcaFontSize);xlabel('\omega','fontsize',labelSize);ylabel('Magnitude','fontsize',labelSize);subplot(2,3,3);plot(w,fwe,'r');set(gca,'FontSize',gcaFontSize);xlabel('\omega','fontsize',labelSize);ylabel('Magnitude','fontsize',labelSize);